          <div className="h-3 w-full bg-tab-active animate-pulse mt-1"></div>
          <div className="h-3 w-full bg-tab-active animate-pulse mt-1"></div>
        </div>

        <div className="flex gap-3 mt-12">
          <div className="h-6 w-1/12 rounded-full bg-tab-active animate-pulse"></div>
          <div className="h-6 w-1/12 rounded-full bg-tab-active animate-pulse"></div>
          <div className="h-6 w-1/12 rounded-full bg-tab-active animate-pulse"></div>
        </div>
      </div>
    </div>
  );
};
export default Details;
